
import random

letters_groups = ["bdg", "ptk", "mn", "fs", "vzj", "lr", "aeiouy", "a", "e", "i", "o", "u", "y"]
group_codes = [chr(ord('a') + i) for i in range(len(letters_groups))]


def gen_letter(group):
    index = random.randint(0, len(group) - 1)
    return group[index]


def gen_pseudo(pattern):
    result = ""
    for group_code in pattern:
        index = ord(group_code) - ord('a')
        result += gen_letter(letters_groups[index])
    return result


def gen_random_pattern(min_len, max_len):
    result = ""
    for i in range(0, random.randint(min_len, max_len)):
        result += gen_letter(group_codes)
    return result


def modified_pattern(pattern):
    return ">" + pattern + "<"


def gen_sub_patterns(pattern):
    mod_pattern = modified_pattern(pattern)
    result = []
    for i in range(len(mod_pattern)):
        for j in range(i + 2, len(mod_pattern)+1):
            if j - i <= 4:
                result += [mod_pattern[i:j]]
    return result


