score_pos = 0
score_neg = 0
score_if_yes = 1
score_if_no = -1


def balance_scores():
    global score_pos, score_neg, score_if_yes, score_if_no
    score_pos = 0
    score_neg = 0
    score_if_yes = 1
    score_if_no = -1
    for item in library.items():
        score = item[1]
        if score > 0:
            score_pos += score
        else:
            score_neg -= score

    if score_pos == 0:
        score_pos = 1
    if score_neg == 0:
        score_neg = 1
    score_if_yes = score_neg // score_pos
    if score_if_yes == 0:
        score_if_yes = 1
        score_if_no = -score_pos // score_neg
    else:
        score_if_no = -1
    MAX_INCR = 10
    if score_if_yes > MAX_INCR:
        score_if_yes = MAX_INCR
    if score_if_no < -MAX_INCR:
        score_if_no = -MAX_INCR

    print("library scores: {} vs {}".format(score_pos, score_neg))
    print("score if yes: {}".format(score_if_yes))
    print("score if no: {}".format(score_if_no))


def pattern_score(pattern):
    result = 0
    for subp in gen_sub_patterns(modified_pattern(pattern)):
        if subp in library:
            result += library[subp]
    return result


LEARNING_MODE = False

if LEARNING_MODE:
    count_fails = 0
    min_score = 0
    balance_scores()

    while True:
        pattern = gen_random_pattern(5, 8)
        score = pattern_score(pattern)
        if score < min_score:
            count_fails += 1
            if count_fails < 100:
                continue
        if count_fails >= 100:
            min_score = int(min_score * 0.8)
        else:
            min_score = int(min_score * 1.05) + 10
        count_fails = 0
        print("min_score: {}".format(min_score))
        print("pattern: {} (score: {})".format(pattern, score))
        for _ in range(10):
            for i in range(10):
                print(gen_pseudo(pattern) + " ", end="")
            print()
        print()

        resp = input("Is it a good pattern ? (y / [n]) ")

        if resp == 'y':
            score = score_if_yes
        else:
            score = score_if_no

        for subp in gen_sub_patterns(pattern):
            if subp in library:
                library[subp] += score
            else:
                library[subp] = score

        print("When your are done, replace the relevant line in the code with the one below:")
